/*global process*/
var vm = require('vm'),
    dust = require('dustjs-linkedin/lib/dust.js'),
    parser = require('dustjs-linkedin/lib/parser.js'),
    compiler = require('dustjs-linkedin/lib/compiler.js');

// use Node equivalents for some Dust methods
var context = vm.createContext({dust: dust});
